mapper.update:
UPDATE SITE_REVIEW
SET review_content = #{review_content}
WHERE review_id = #{review_id}

mapper.select:

		SELECT *
		FROM (
    	select rownum rn, review_id, site_id, review_content, review_name, review_date, review_star
    	from site_review
    	where site_id =  #{site_id}
   		 and review_id > 0
   		 and rownum <= #{cri.pageNum} * #{cri.amount}
   		) where rn > (#{cri.pageNum} -1) * #{cri.amount}
