mapper.update:
update member set sex=#{sex}, date_of_birth=#{date_of_birth},
				  address=#{address}, profession=#{profession},
				  active_hours=#{active_hours}, question1=#{question1},
				  answer1=#{answer1}, question2=#{question2}, answer2=#{answer2},
				  question3=#{question3}, answer3=#{answer3}
			  	  where member_id=#{member_id}
